mapper.select:
SELECT count(*)
FROM (
	SELECT * FROM GOOD

		AND userno = #{userno}
		AND boardno = #{boardno}

)

mapper.delete:
DELETE recommend

	AND userno = #{userno}
	AND boardno = #{boardno}

mapper.select:
