mapper.update:
UPDATE board
set boardView = boardView + 1

	and boardno = #{boardNo}

UPDATE board


		title = #{title},


		content = #{content},

	updatedate = #{updateDate}


	boardno = #{boardNo}

mapper.select:
SELECT count(*)
FROM (
	SELECT * FROM GOOD

		AND userno = #{userNo}
		AND boardno = #{boardNo}

)

mapper.delete:
DELETE GOOD

	AND userno = #{userNo}
	AND boardno = #{boardNo}

mapper.select:
SELECT * FROM (
	SELECT count(*) totalRecommend FROM (
		SELECT * FROM GOOD
		WHERE boardno = #{boardNo}
		)
	) ,(
	SELECT count(*) isRecommend FROM (
		SELECT * FROM GOOD
		WHERE boardno = #{boardNo}
		AND userno = #{userNo}
		)
	)

SELECT count(*) recomm FROM (
	SELECT * FROM GOOD
	WHERE boardno = #{boardNo}
	)

SELECT totalRecomm, isRecomm  FROM (
	SELECT count(*) totalRecomm FROM(
		SELECT boardno, userno FROM GOOD
		WHERE boardno = #{boardNo})
	) B, 
(	SELECT count(*) isRecomm FROM(
		SELECT * FROM GOOD
		WHERE boardno = #{boardNo}
		AND userno=#{userNo}
		)
	) A

SELECT count(*) FROM (
    select ACCESSNO, USERNO, ACCESSDATE from ACCESSHISTORY
)

