SELECT b.*, NVL(good, 0) good FROM (SELECT * FROM board
and boardNo IN
#{data.boardNo}
) b left OUTER join (SELECT BOARDNO, COUNT(BOARDNO) as good FROM GOOD GROUP BY BOARDNO ORDER BY boardno DESC) g ON b.boardno = g.boardno ORDER BY b. boardNo DESC

