mapper.update:
update CALENDAR
set calendar_title = #{calendarTitle}
, content = #{content}

    AND userno = #{userno}
    AND start1 = #{start1}
    AND end1 = #{end1}

mapper.delete:
delete CALENDAR

    AND userno = #{userno}
    AND calendar_title = #{calendarTitle}
    and content = #{content}
    and start1 = #{start1}
    and end1 = #{end1}
