mapper.select:
SELECT * FROM (
	SELECT rownum rnum, B.* FROM (


		ORDER BY ItemNo DESC
		) B
	)

	AND rnum BETWEEN #{startNo } AND #{endNo}

SELECT it.itemno, stored_Name storedName, origin_Name originName , fileNo FROM item it, itemFile ItF
AND it.itemno = itf.itemno AND it.titleimg = itf.FILENO AND it.itemno IN
#{data.itemNo}

mapper.insert:
INSERT into itemfile(fileNo,itemNo,originName,storedName) SELECT itemfile_seq.nextval, D.* FROM (
SELECT #{file.itemNo} AS boardNo ,#{file.originName} AS origin_name ,#{file.storedName} AS stored_name FROM DUAL
) D

mapper.update:
UPDATE item
set titleImg = #{fileNo}

	AND itemNo = #{itemNo}

mapper.select:
SELECT itemno,userno, itemname,createdate,price,itemcomm,remain,titleImg
FROM item

	AND itemno = #{itemNo}

SELECT fileNo, itemNo, origin_name, stored_name
FROm itemFILE

	AND itemNo = #{itemNo}

mapper.delete:
DELETE FROM ItemFile
WHERE itemNo = #{itemNo}
AND fileNo != (SELECT titleImg FROM Item WHERE itemNo = #{itemNo})
