mapper.select:
select userid from usertb
where name = #{name }
and email = #{email }

select userpw from usertb
where userid = #{userid }
  and  name = #{name }
  and email = #{email }

mapper.update:
update usertb
set userpw = CRYPTO.ENCRYPT_AES(#{userpw})
where userid = #{userid }
  and  name = #{name }
  and email = #{email }

UPDATE usertb
SET BLACK = 'Y'
WHERE userno = #{userno}

UPDATE usertb
SET BLACK = 'N'
WHERE userno = #{userno}

mapper.select:
select count(*) from ( SELECT userno, nickname FROM usertb
and gradeno < 5000
)

	SELECT * from
	(	select rownum rnum, u.* FROM
		(        SELECT userno,userid,name,NICKNAME,GENDER,gradeno,black FROM usertb

            and gradeno  < 5000 


		order by userno
		)u
	)

		and rnum between #{startNo} and #{endNo}
